import{_ as e,c as i,o as t,ah as o}from"./chunks/framework.CH-e-O8w.js";const h=JSON.parse('{"title":"Options when creating an application","description":"","frontmatter":{"head":[["meta",{"name":"og:site_name","content":"Vue webapp builder"}],["meta",{"name":"og:image","content":"/images/vue-webapp-logo.png"}],["meta",{"name":"twitter:image","content":"/images/vue-webapp-logo.png"}]]},"headers":[],"relativePath":"en/vue-webapp/options/index.md","filePath":"en/vue-webapp/options/index.md","lastUpdated":1738480984000}'),n={name:"en/vue-webapp/options/index.md"};function l(r,a,p,s,c,d){return t(),i("div",null,a[0]||(a[0]=[o('<h1 id="options-when-creating-an-application" tabindex="-1">Options when creating an application <a class="header-anchor" href="#options-when-creating-an-application" aria-label="Permalink to &quot;Options when creating an application&quot;">​</a></h1><h2 id="available-options" tabindex="-1">Available options <a class="header-anchor" href="#available-options" aria-label="Permalink to &quot;Available options&quot;">​</a></h2><p>The following options are currently available for configuration:</p><ul><li>Layout - application layout</li><li>Header - top part in the application template</li><li>Footer - bottom part of the application template</li><li>Drawer - navigation bar</li><li>Navbar - navigation menu (inside the drawer).</li><li>Github Pages deploy Workflow - GitHub Actions script for build and deploy application to GitHub Pages</li><li>PWA - addition of Service worker and Manifest to the application</li><li>API module - abstraction layer for communication with backend API</li><li>JSON-RPC - adapter for API module</li><li>Splash screen - splash screen during application startup to improve UX</li><li>Google analytics - code for integration of Google analytics</li><li>Open graph - meta tags for building snippets by social networks</li></ul><h2 id="available-by-default" tabindex="-1">Available by default <a class="header-anchor" href="#available-by-default" aria-label="Permalink to &quot;Available by default&quot;">​</a></h2><ul><li>Dark theme. All plugins support theme switching</li><li>BaseIcon - icon component with the ability to dynamically specify a name</li><li>BaseToggle - toggle component</li></ul><h2 id="in-development" tabindex="-1">In development <a class="header-anchor" href="#in-development" aria-label="Permalink to &quot;In development&quot;">​</a></h2><ul><li>Global Preloader</li><li>i18n - internationalization of the application using different variants of <code>i18n</code> libraries.</li></ul><h2 id="planned" tabindex="-1">Planned <a class="header-anchor" href="#planned" aria-label="Permalink to &quot;Planned&quot;">​</a></h2><ul><li>Online showcase for dynamic testing of different variants of application parts and functionality</li><li>Expanding the number of variants of layout, header, footer and other components</li><li>Auth - module framework for authentication on JWT tokens, including different variants of registration and login forms.</li><li>Auth integration with popular service providers (Firebase and others).</li><li>GitHub Actions for application deployment to various services (Vercel, Heroku, Firesbase) and other useful CI/CD scripts.</li><li>Check on startup for application new version and reload to avoid browser cache issues</li><li>Themes - application themes (include both different color palettes and the ability to change other CSS parameters for deep customization of the templates and components)</li></ul>',10)]))}const m=e(n,[["render",l]]);export{h as __pageData,m as default};
