import{_ as t,c as i,o as e,ah as l}from"./chunks/framework.CH-e-O8w.js";const m=JSON.parse('{"title":"Цели и задачи проекта","description":"","frontmatter":{"pageClass":"arty-crafty","head":[["meta",{"name":"og:site_name","content":"Arty-Crafty - проект онлайн магазина"}],["meta",{"name":"og:image","content":"/images/arty-crafty-logo.png"}],["meta",{"name":"twitter:image","content":"/images/arty-crafty-logo.png"}]]},"headers":[],"relativePath":"ru/arty-crafty/introduction/goals.md","filePath":"ru/arty-crafty/introduction/goals.md","lastUpdated":1738480984000}'),r={name:"ru/arty-crafty/introduction/goals.md"};function o(s,a,n,c,d,u){return e(),i("div",null,a[0]||(a[0]=[l('<h1 id="цели-и-задачи-проекта" tabindex="-1">Цели и задачи проекта <a class="header-anchor" href="#цели-и-задачи-проекта" aria-label="Permalink to &quot;Цели и задачи проекта&quot;">​</a></h1><h2 id="цели-данного-проекта" tabindex="-1">Цели данного проекта: <a class="header-anchor" href="#цели-данного-проекта" aria-label="Permalink to &quot;Цели данного проекта:&quot;">​</a></h2><ol><li>Постепенное построение продукта: начиная с витрины с простым статичным бэкендом в форме json файлов и заканчивая полноценным маркетплейсом со сторонним headless e-commerce решением.</li><li>При разработке должны использоваться последние проверенные достижения Vue.js экосистемы и лучшие практики в разработке программного обеспечения.</li><li>Проект должен быть примером грамотной реализации проекта по разработке ПО, начиная с архитектуры и заканчивая кодом.</li><li>Программная система должна быть удобна для использования, кастомизации и развертывания для человека, не знакомого с Vue, - то есть, представлять из себя продукт для конечного пользователя.</li></ol><h2 id="нефункциональные-требования-software-quality-attributes" tabindex="-1">Нефункциональные требования (software quality attributes) <a class="header-anchor" href="#нефункциональные-требования-software-quality-attributes" aria-label="Permalink to &quot;Нефункциональные требования (software quality attributes)&quot;">​</a></h2><ol><li>Высокий UX</li><li>Производительность</li><li>Безопасность</li><li>Функциональность</li><li>Расширяемость</li><li>Понятный задокументированный чистый код, использование лучших практик</li></ol><h2 id="общие-функциональные-требования" tabindex="-1">Общие функциональные требования <a class="header-anchor" href="#общие-функциональные-требования" aria-label="Permalink to &quot;Общие функциональные требования&quot;">​</a></h2><ol><li>PWA</li><li>Глубоко настраиваемые темы. Как общие, так и для каждого магазина маркетплейса.</li><li>i18n (интернационализация)</li></ol><h2 id="этапы-развития-приложения-приблизительно" tabindex="-1">Этапы развития приложения (приблизительно) <a class="header-anchor" href="#этапы-развития-приложения-приблизительно" aria-label="Permalink to &quot;Этапы развития приложения (приблизительно)&quot;">​</a></h2><ol><li>Витрина (показ каталога категорий и товаров из них)</li><li>Бэкенд и база данных</li><li>Поиск по товарам, маркетплейс (отдельные магазины)</li><li>UI/UX</li><li>Интеграция с E-commerce headless решением</li><li>Аутентификация и авторизация</li><li>Корзина для покупок, оформление заказов, личный кабинет покупателя</li><li>Добавление различного функционала: методы оплаты, доставки, рейтинги товаров, отзывы покупателей, customer service</li><li>SEO оптимизация</li><li>Административная панель для магазинов маркетплейса</li></ol><p>Разработка будет вестись постепенно, по этапам, описанным выше. После каждого этапа должно получаться рабочее (продуктовое) приложение. Разбиение большой задачи на небольшие подзадачи и всего процесса стадии сильно упрощает управление проектом и повышает вероятность итогового положительного исхода.</p>',10)]))}const f=t(r,[["render",o]]);export{m as __pageData,f as default};
