import{_ as t,c as a,o as i,ah as l}from"./chunks/framework.CH-e-O8w.js";const c=JSON.parse('{"title":"Данные релизов, коммитов и пул риквестов","description":"","frontmatter":{"pageClass":"release-timeline","head":[["meta",{"name":"og:site_name","content":"Release timeline - visual component displaying GitHub release timeline"}],["meta",{"name":"og:image","content":"/images/vue-faq-logo.png"}],["meta",{"name":"twitter:image","content":"/images/vue-faq-logo.png"}]]},"headers":[],"relativePath":"ru/release-timeline/getting-data.md","filePath":"ru/release-timeline/getting-data.md","lastUpdated":1738480879000}'),n={name:"ru/release-timeline/getting-data.md"};function s(r,e,o,m,p,u){return i(),a("div",null,e[0]||(e[0]=[l('<h1 id="данные-релизов-коммитов-и-пул-риквестов" tabindex="-1">Данные релизов, коммитов и пул риквестов <a class="header-anchor" href="#данные-релизов-коммитов-и-пул-риквестов" aria-label="Permalink to &quot;Данные релизов, коммитов и пул риквестов&quot;">​</a></h1><h2 id="получение-данных" tabindex="-1">Получение данных <a class="header-anchor" href="#получение-данных" aria-label="Permalink to &quot;Получение данных&quot;">​</a></h2><p>Есть три варианта получения данных с releases, pull requests и commits с GitHub-а</p><ol><li>Динамическое. Прямой запрос к GitHub REST API во время открытия страницы.</li><li>Запрос к GitHub REST API через прокси</li><li>Запрос к GitHub REST API во время билда проекта, оптимизация и сохранение данных локально на сайте.</li></ol><p>На данный момент непосредственно реализованы первый и второй вариант, есть возможность указать <code>url</code> для запросов releases, pull requests и commits. По умолчанию используется первый.</p><p>Второй вариант нужен для уменьшения размера загружаемого файла., так как 95% данных в ответах GitHub - не используется в программе.</p><p>Формат данных при этом должен быть как у GitHub, но может содержать только нужные поля.</p><p>Третий вариант также может оптимизировать потоки данных, но делает снэпшот релизов и пул риквестов на определенный момент времени. При выходе нового релиза потребуется пересобрать документацию. В данном случае также нужно указать корректный url. Реализовывать логику третьего варианта (получение, фильтрация, сохранение даннах) необходимо пользователю пакета.</p>',8)]))}const _=t(n,[["render",s]]);export{c as __pageData,_ as default};
