import{_ as a,c as t,o as i,ah as r}from"./chunks/framework.CH-e-O8w.js";const n="/assets/rt.Ctjj4VhI.jpg",u=JSON.parse('{"title":"Release Timeline","description":"","frontmatter":{"pageClass":"release-timeline","head":[["meta",{"name":"og:site_name","content":"Release timeline - visual component displaying GitHub release timeline"}],["meta",{"name":"og:image","content":"/images/vue-faq-logo.png"}],["meta",{"name":"twitter:image","content":"/images/vue-faq-logo.png"}]]},"headers":[],"relativePath":"ru/release-timeline/index.md","filePath":"ru/release-timeline/index.md","lastUpdated":1738480879000}'),l={name:"ru/release-timeline/index.md"};function o(s,e,m,d,c,p){return i(),t("div",null,e[0]||(e[0]=[r('<h1 id="release-timeline" tabindex="-1">Release Timeline <a class="header-anchor" href="#release-timeline" aria-label="Permalink to &quot;Release Timeline&quot;">​</a></h1><h2 id="визуализация-релизов" tabindex="-1">Визуализация релизов <a class="header-anchor" href="#визуализация-релизов" aria-label="Permalink to &quot;Визуализация релизов&quot;">​</a></h2><p>Визуализация релизов любого GitHub репозитория по временной шкале, с информацией о коммитах и пул риквестах.</p><p>Встраивается в VitePress, может быть хорошей заменой <code>changelog</code>-а в технической документации проекта, автоматизируя фиксацию изменений и делая слежение за ними удобным для пользователей.</p><p>Также может быть подключено в любой HTML странице как Web component или микрофронтенд.</p><p><img src="'+n+'" alt="image"></p><p>Библиотека написана с использованием Vue 3, не имеет зависимостей, занимает 15Кб в распакованном виде. Адаптирована для работы в VitePress (переключение светлой и темной тем). Может подключаться как <code>web component</code> в не Vue проектах.</p><p>Данные по релизам определенного репозитория берутся через GitHub REST API.</p><p>Public репозитории доступны сразу, для private репозиториев требуется использовать GitHub API токен.</p><h2 id="репозитории" tabindex="-1">Репозиторий <a class="header-anchor" href="#репозитории" aria-label="Permalink to &quot;Репозиторий&quot;">​</a></h2><ul><li><a href="https://github.com/vuesence/release-timeline" target="_blank" rel="noreferrer">GitHub</a></li></ul><h2 id="demo" tabindex="-1">Demo <a class="header-anchor" href="#demo" aria-label="Permalink to &quot;Demo&quot;">​</a></h2><ul><li><a href="https://vuesence.github.io/release-timeline/" target="_blank" rel="noreferrer">Demo</a> (можно задать любой репозиторий через выбор в верхнем левом углу)</li></ul><h2 id="credits" tabindex="-1">Credits <a class="header-anchor" href="#credits" aria-label="Permalink to &quot;Credits&quot;">​</a></h2><p><a href="https://codepen.io/sarazond/pen/LYGbwj" target="_blank" rel="noreferrer">Parallax Star background in CSS</a></p>',15)]))}const _=a(l,[["render",o]]);export{u as __pageData,_ as default};
