import{_ as i,c as t,o as a,ah as l}from"./chunks/framework.CH-e-O8w.js";const m=JSON.parse('{"title":"Принципы","description":"","frontmatter":{"head":[["meta",{"name":"og:site_name","content":"Vue webapp builder"}],["meta",{"name":"og:image","content":"/images/vue-webapp-logo.png"}],["meta",{"name":"twitter:image","content":"/images/vue-webapp-logo.png"}]]},"headers":[],"relativePath":"ru/vue-webapp/guidelines.md","filePath":"ru/vue-webapp/guidelines.md","lastUpdated":1738480879000}'),o={name:"ru/vue-webapp/guidelines.md"};function n(p,e,r,s,u,c){return a(),t("div",null,e[0]||(e[0]=[l('<h1 id="принципы" tabindex="-1">Принципы <a class="header-anchor" href="#принципы" aria-label="Permalink to &quot;Принципы&quot;">​</a></h1><ul><li>Лёгкое эффективное вебприложение с высоким современным UI/UX</li><li>Vue 3, Composition API, script setup синтаксис</li><li>Не использовать сторонние библиотеки без необходимости. Единственная обязательная зависимость - <code>vue-router</code></li><li>Использование чистого и эффективного HTML5 и CSS3 для верстки</li><li>Подключение требуемого функционала только по необходимости</li><li>Высокая кастомизация</li></ul><hr><ul><li><p>Подключение определенной функциональности, например i18n, означает не просто добавление этой библиотеки в <code>package.json</code>, а полноценную минимальную работающую интеграцию - создание соответствующей composable функции, нескольких локалей, и применение t() в шаблоне для иллюстрации с необходимыми комментариями в коде для облегчения последующего использования и кастомизации.</p></li><li><p>Подключение определенной функциональности, использующей стороннюю библиотеку (например, тостер или модальное окно), делается через обёртку, чтобы потом разработчику при желании было легко поменять конкретный пакет, реализующий данный функционал.</p></li><li><p>Для веб приложения используется <strong>TypeScript</strong>, однако продолжать писать приложение можно на чистом JavaScript.</p></li><li><p>В качестве сборщика используется Vite.</p></li><li><p>В качестве CSS препроцессора используется SCSS.</p></li><li><p>Для линтинга и форматирования в ESlint используется <a href="https://github.com/antfu/eslint-config" target="_blank" rel="noreferrer">eslint-config</a> Antony Fu.</p></li><li><p>На &quot;выходе&quot; получается готовое Vue 3 приложение без зависимостей от каких-либо фреймворков/библиотек/пакетов, если только пользователь явно не включил их в каркас.</p></li></ul>',4)]))}const _=i(o,[["render",n]]);export{m as __pageData,_ as default};
