import{_ as a,c as t,o as i,ah as l}from"./chunks/framework.CH-e-O8w.js";const m=JSON.parse('{"title":"Цели","description":"","frontmatter":{"head":[["meta",{"name":"og:site_name","content":"Vue webapp builder"}],["meta",{"name":"og:image","content":"/images/vue-webapp-logo.png"}],["meta",{"name":"twitter:image","content":"/images/vue-webapp-logo.png"}]]},"headers":[],"relativePath":"ru/vue-webapp/objectives.md","filePath":"ru/vue-webapp/objectives.md","lastUpdated":1738480879000}'),o={name:"ru/vue-webapp/objectives.md"};function r(n,e,s,p,c,u){return i(),t("div",null,e[0]||(e[0]=[l('<h1 id="цели" tabindex="-1">Цели <a class="header-anchor" href="#цели" aria-label="Permalink to &quot;Цели&quot;">​</a></h1><ol><li>Создание конструктора каркаса простого сразу работающего SPA приложения под конкретную задачу за пару минут, с возможностью его дальнейшей стилизации и наполнения контентом.</li><li>Тонкая кастомизация его с помощью небольших функциональных блоков кода.</li><li>Сбор и обобщение лучших техник и практик при создании современного реактивного фронтенда.</li><li>Дать возможность начинающим разработчикам на готовых примерах ознакомиться с экосистемой Vue.js, изучить лучшие практики фронтенда и Vue 3 от построения архитектуры приложения до наименования CSS классов.</li><li>Дать возможность опытным разработчикам быстро и эффективно создавать каркас готового веб приложения и использовать переиспользуемые блоки кода (boilerplate), особенно такие, которые не могут быть распространяемы как NPM пакеты.</li></ol><p>Данное приложение должно выполнять роль конструктора каркаса вебсайта, с возможностью выбора при создании:</p><ul><li>Глобального бизнес-шаблона сайта (портфолио, блог, онлайн-магазин и.т.д)</li><li>Варианта общего лэйаута вебприложения</li><li>Вариантов конкретных компонент (header, footer, navigation drawer и т.д.)</li><li>Варианта общего стиля/дизайна</li><li>Отдельного функционала, инжектируемого как функциональные фрагменты (API модуль, i18n, PWA, splash screen, Auth модуль, themes и т.д. )</li><li>Вариантов GitHub Actions с возможностью билда и деплоя на популярные хостинги</li><li>Возможности подключения/интеграции при желании распространенных библиотек (в том числе UI)</li><li>Возможности интеграции c определенными headless CRM и API сервисами</li></ul><p>Отдельно планируется создание веб-витрины, на которой пользователь, динамично меняя параметры, сможет посмотреть и протестировать доступные макеты сайта, темы/стилизации, компоненты (header, footer и т.д.) и функционал развертываемого приложения, а также их комбинацию друг с другом.</p>',5)]))}const _=a(o,[["render",r]]);export{m as __pageData,_ as default};
