import{_ as e,o as a,c as i,a4 as l}from"./chunks/framework.B8KxSsRZ.js";const m=JSON.parse('{"title":"Опции при создании приложения","description":"","frontmatter":{"head":[["meta",{"name":"og:site_name","content":"Vue webapp builder"}],["meta",{"name":"og:image","content":"/images/vue-webapp-logo.png"}],["meta",{"name":"twitter:image","content":"/images/vue-webapp-logo.png"}]]},"headers":[],"relativePath":"ru/vue-webapp/options/index.md","filePath":"ru/vue-webapp/options/index.md","lastUpdated":1729563759000}'),t={name:"ru/vue-webapp/options/index.md"},o=l('<h1 id="опции-при-создании-приложения" tabindex="-1">Опции при создании приложения <a class="header-anchor" href="#опции-при-создании-приложения" aria-label="Permalink to &quot;Опции при создании приложения&quot;">​</a></h1><h2 id="доступные-опции" tabindex="-1">Доступные опции <a class="header-anchor" href="#доступные-опции" aria-label="Permalink to &quot;Доступные опции&quot;">​</a></h2><p>На данный момент доступно конфигурирование следующих опций:</p><ul><li>Layout - макет приложения</li><li>Header - верхняя часть в макете приложения</li><li>Footer - нижняя часть в макете приложения</li><li>Drawer - навигационная шторка</li><li>Navbar - навигационное меню (внутри шторки)</li><li>Github Pages deploy Workflow - GitHub Actions сценарий для билда и деплоя приложения на GitHub Pages</li><li>PWA - добавление Service worker и Манифеста к приложению</li><li>API модуль - абстрактный слой для связи с бэкенд API</li><li>JSON-RPC - адаптер для API модуля</li><li>Splash screen - экран-заставка во время старта приложения для улучшения UX</li><li>Google analytics - код для подключения статистики</li><li>Open graph - мета тэги для построения сниппетов соцсетями</li></ul><h2 id="доступно-по-умолчанию" tabindex="-1">Доступно по умолчанию <a class="header-anchor" href="#доступно-по-умолчанию" aria-label="Permalink to &quot;Доступно по умолчанию&quot;">​</a></h2><ul><li>Dark theme - темная тема. Все подключаемые компоненты поддерживают переключение тем</li><li>BaseIcon - компонент иконки с возможностью динамичного указания имени</li><li>BaseToggle - компонент переключателя</li></ul><h2 id="в-разработке" tabindex="-1">В разработке <a class="header-anchor" href="#в-разработке" aria-label="Permalink to &quot;В разработке&quot;">​</a></h2><ul><li>Глобальный прелоадер</li><li>i18n - интернационализация приложения с помощью различных вариантов <code>i18n</code> библиотек.</li></ul><h2 id="в-планах" tabindex="-1">В планах <a class="header-anchor" href="#в-планах" aria-label="Permalink to &quot;В планах&quot;">​</a></h2><ul><li>Онлайн витрина для динамичного тестирования различных вариантов частей приложения и функционала</li><li>Расширение числа вариантов layout, header, footer и других компонент</li><li>Auth - каркас модуля для аутентификации на JWT токенах, включая различные варианты форм регистрации и логина</li><li>Интеграция Auth с популярными сервис провайдерами (Firebase и др.)</li><li>GitHub Actions для деплоя приложения на различные сервисы (Vercel, Heroku, Firesbase) и другие полезные CI/CD сценарии.</li><li>Проверка при старте на свою новую версию и перезагрузка для избежания проблем с кэшем браузера</li><li>Themes - темы приложения (включают в себя как разные цветовые палитры, так и возможность менять другие CSS параметры для глубокой кастомизации шаблона и компонентов)</li></ul>',10),r=[o];function n(s,u,c,d,h,p){return a(),i("div",null,r)}const b=e(t,[["render",n]]);export{m as __pageData,b as default};
